mapper.select:
select course.course_id,course.teacher_id,teacher.teacher_name,course.course_name,course.course_cover

from course,teacher

where course.teacher_id = teacher.teacher_id
and teacher.teacher_id = #{teacher_id}


    and course.course_status = #{status}


limit #{start},#{size};

mapper.update:
update course

    course_name=#{course_name},
    course_cover=#{course_cover},
    course_status=#{course_status}

where course_id=#{course_id} and teacher_id = #{teacher_id}
